def strip_loose_sig_lines(txt: str) -> tuple[str, bool]:
    #      "sig = None"
    lines = txt.splitlines(); changed = False
    new_lines = []
    for l in lines:
        if re.match(r"^\s*sig\s*=", l):
            changed = True
            continue
        new_lines.append(l)
    return "\n".join(new_lines) + ("\n" if not txt.endswith("\n") else ""), changed

